/*
Power digit sum

2^15 = 32768 and the sum of its digits is 3 + 2 + 7 + 6 + 8 = 26.

What is the sum of the digits of the number 2^1000?
*/

const TWO_TO_POWER_ONE_THOUSAND = ''

const findPowerDigitSum = () => {
  const digits = TWO_TO_POWER_ONE_THOUSAND.split('')

  let result = 0

  for (const digit of digits) {
    result += Number(digit)
  }

  return result
}

console.log(findPowerDigitSum())
